import base64
import io
from PIL import Image

dna_icon_b64 = b''

swap_b64 = b''


def loadImage(Bytes):
    image = io.BytesIO(base64.b64decode(Bytes))
    image = Image.open(image)
    image.thumbnail((32, 32), Image.ANTIALIAS)
    return image


dna_icon = loadImage(dna_icon_b64)
swap = loadImage(swap_b64)
